import { useState, useEffect } from 'react';
import api from '../services/api';

function Admin() {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({ name: '', category: '', description: '', price: '', stock: '', imageUrl: '' });

  useEffect(() => {
    api.get('/products').then(res => setProducts(res.data));
  }, []);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await api.post('/products', form, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Product added');
      setForm({ name: '', category: '', description: '', price: '', stock: '', imageUrl: '' });
    } catch {
      alert('Failed to add product');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Admin Panel</h2>
      <form onSubmit={handleSubmit} style={{ marginBottom: '2rem' }}>
        <input name="name" placeholder="Name" value={form.name} onChange={handleChange} required /><br /><br />
        <input name="category" placeholder="Category" value={form.category} onChange={handleChange} required /><br /><br />
        <textarea name="description" placeholder="Description" value={form.description} onChange={handleChange} required /><br /><br />
        <input name="price" type="number" placeholder="Price" value={form.price} onChange={handleChange} required /><br /><br />
        <input name="stock" type="number" placeholder="Stock" value={form.stock} onChange={handleChange} required /><br /><br />
        <input name="imageUrl" placeholder="Image URL" value={form.imageUrl} onChange={handleChange} required /><br /><br />
        <button type="submit">Add Product</button>
      </form>
      <h3>All Products</h3>
      {products.map(product => (
        <div key={product._id} style={{ border: '1px solid #ccc', padding: '1rem', marginBottom: '1rem' }}>
          <h4>{product.name}</h4>
          <p>Category: {product.category}</p>
          <p>Price: ${product.price}</p>
          <p>Stock: {product.stock}</p>
        </div>
      ))}
    </div>
  );
}

export default Admin;
