import { useEffect, useState } from 'react';
import api from '../services/api';
import { Link } from 'react-router-dom';

function Home({ handleLogout }) {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    api.get('/products').then(res => setProducts(res.data));
  }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Welcome</h1>
      <button onClick={handleLogout}>Logout</button>
      <h2>All Products</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {products.map(product => (
          <div key={product._id} style={{ border: '1px solid #ccc', margin: '1rem', padding: '1rem', width: '200px' }}>
            <h4>{product.name}</h4>
            <p>{product.category}</p>
            <p>${product.price}</p>
            {product.images.length > 0 && <img src={product.images[0]} alt="product" style={{ width: '100%' }} />}
            <Link to={`/category/${product.category.toLowerCase()}`}>View More</Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
